import request from 'request';

const setAuthToken = token => {
  if (token) {
    // Apply authorization token to every request if logged in
    request.headers.auth = token;
  } 
  else {
    // Delete auth header
    delete request.headers.auth;
  }
};

export default setAuthToken;
