import request from 'request';
import serverConf from '../config'
import { GET_ERRORS, UPDATE_USER } from './types';
import { loginUser } from './authActions'


export const updateAdmin = (id, form) => dispatch => {

    const endpoint = `${serverConf.uri}${serverConf.endpoints.admin.update}/${id}`;

    request.put(endpoint, { form }, (error, response, body) => {

        const res = JSON.parse(body);


        if (error) {
            dispatch({
                type: GET_ERRORS,
                payload: res
              })
        }
        else {

            dispatch({
               //type: UPDATE_USER,

               // payload shoukld contain new user info
               //payload: res
            })


        }   
    });
 };

 // get volunteers from database
export const getAdmin = () => dispatch => {

    const endpoint = `${serverConf.uri}${serverConf.endpoints.admin.fetch}`;

    request.get(endpoint, (error, response, body) => {

        const res = JSON.parse(body);

        if (error) {
            dispatch({
                type: GET_ERRORS,
                payload: res
              })
        }
        else {
            // set current volunteers
            dispatch(setCurrentUser(res))
        }    
    });
};

export const setCurrentUser = user => {
    return {
        type: UPDATE_USER,
        payload: user
    };
};
