        transform: 'scale(0.8)',
    },
    title: {
        fontSize: 14,
    },
    pos: {
        marginBottom: 12,
    },
    blue: {
        backgroundColor: blue[500],
    },
    Avatar: {
        marginBottom: 15,
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
    }
  };
// Login Styling END

class LoginForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            firstName: "Aurelien",
            lastName: "Meray",
            email: "aurie@test.com",
            phoneNumber: "305-898-7898",
        }
    }

    componentDidMount() {

    }

    //Supress depricated warning use UNSAFE_
    UNSAFE_componentWillReceiveProps(nextProps) {

    }

    handleInput = (e) =>{
        const value = e.target.value
        const name = e.target.name

        this.setState({
        [name]: value 
        })
    }


  render(){   
    return (
        <Grid
        container
        spacing={0}
        direction="column"
        alignItems="center"
        justify="center">
            <Card 
            className={this.props.classes.card} 
            variant="outlined"
            justify="center">
                <CardContent>
                    <Grid
                    className={this.props.classes.Avatar}
                    container
                    spacing={0}
                    direction="column"
                    alignItems="center"
                    justify="center"
                    >
                        <Avatar className={this.props.classes.blue} >AM</Avatar>
                    </Grid>
                    <form className={this.props.classes.form} noValidate>



                    </form>


                    {/* <Typography className={this.props.classes.title} color="textSecondary" gutterBottom>
                        Profile Information
                    </Typography>
                    <Typography variant="h5" component="h2">
                        benevolent
                    </Typography>
                    <Typography className={this.props.classes.pos} color="textSecondary">
                        adjective
                    </Typography>
                    <Typography variant="body2" component="p">
                        well meaning and kindly.
                        <br />
                        {'"a benevolent smile"'}
                    </Typography> */}
                </CardContent>
                <CardActions>
                    <Button size="small">Learn More</Button>
                </CardActions>
            </Card>

        </Grid>

    );
  }
}

// define types
LoginForm.propTypes = {
  classes: PropTypes.object.isRequired,
  loginUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

// allows us to get our state from Redux and map it to props
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect (
  mapStateToProps
)(withRouter(withStyles(useStyles)(LoginForm)));
