            </Switch>
          </Fragment> */}
          <Redirect from="/" to="/login"/>
          <NavBar/>


          <Route path="/login" component={ () => <Login update={this.forceUpdate}/>} />
          <Route path="/dashboard" component={Dashboard}/> 


        </Router>
			</div>
		);
	}
}


App.propTypes = {
	classes: PropTypes.object.isRequired,
};

export default App;
