import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import LockRoundedIcon from '@material-ui/icons/LockRounded';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import { blueGrey, blue, red } from '@material-ui/core/colors';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import PhoneIcon from '@material-ui/icons/Phone';
import FavoriteIcon from '@material-ui/icons/Favorite';
import PersonPinIcon from '@material-ui/icons/PersonPin';


const theme = createMuiTheme({
    palette: {
      primary: blue,
    }
  });

const useStyles = makeStyles(theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: blueGrey[500],
    '&:hover': {
        backgroundColor: blue[500],
    }
  },
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
    backgroundColor: blueGrey[700],
    color: "white",
    fontWeight: "bold",
    '&:hover': {
        backgroundColor: blue[500],
    }
  },
  root: {
    flexGrow: 1,
    height: 100,
    minWidth: 500,
    maxWidth: 350,
  },
}));

export default function LoginForm() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <ThemeProvider theme={theme}>
    <Container component="main" maxWidth="xs">
      <CssBaseline />
      <div className={classes.paper}>
        <Avatar className={classes.avatar}>
        <LockRoundedIcon />
        </Avatar>
        <Typography component="h1" variant="h5">
          VAS Login
        </Typography>
        <form className={classes.form} noValidate>
          <TextField
            variant="outlined"
            color= "primary"
            margin="normal"
            required
            fullWidth
            id="email"
            label="Email Address"
            name="email"
            autoComplete="email"
            autoFocus
          />
          <TextField
            variant="outlined"
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
          />

          <Paper square className={classes.root} centered>
            <Tabs
              value={value}
              onChange={handleChange}
              variant="standard"
              indicatorColor="secondary"
              textColor="secondary"
              aria-label="icon label tabs example"
              centered
            >
              <Tab icon={<PhoneIcon />} label="Admin" />
              <Tab icon={<FavoriteIcon />} label="Volunteer" />
              <Tab icon={<PersonPinIcon />} label="School Admin" />
            </Tabs>
          </Paper>

          <Button
            type="submit"
            fullWidth
            variant="contained"
            className={classes.submit}
          >
            Login
          </Button>
        </form>
      </div>
    </Container>
    </ThemeProvider>
  );
}
