import { SET_CURRENT_USER, USER_LOADING, UPDATE_USER, SET_ADMIN} from '../actions/types';
import isEmpty from 'is-empty';

  const initialState = {
    admin: {},
    loading: false
  };

  export default function(state = initialState, action) {
    switch (action.type) {
        case SET_ADMIN:
        return {
          ...state,
          admin: action.payload
        };
      case USER_LOADING:
        return {
          ...state,
          loading: true
        };
      default:
        return state;
    }
  }
