import React, { Component } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { createMuiTheme } from '@material-ui/core/styles';
import { ThemeProvider } from '@material-ui/core/styles';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import { Link } from "react-router-dom";
import { logoutUser } from "../actions/authActions";
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";

const theme = createMuiTheme({
    palette: {
      primary: {
          main: "#455a64"
      },
    }
  });

const useStyles = {
  root: {
    flexGrow: 1,
  },
  toolbar: {
    display: 'felx',
    minHeight: 45,
    maxHeight:64,
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
    color: 'white',
  },
  login: {
    color: 'white !important'
  },
  link: {
    textDecoration: 'none'
  }
};

// submitLogout = async (e) =>{
//   e.preventDefault()

//   this.props.logoutUser(); 
// }

class NavBar extends Component {
render(){
  return (
    <ThemeProvider theme={theme}>
    <div className={this.props.classes.root}>
      <AppBar position='static'>
        <Toolbar className={this.props.classes.toolbar}>
          <Link to='/' className={this.props.classes.link}>
            <Typography className={this.props.classes.title} variant='h5'>
            Volunteer Attendance System
            </Typography>
          </Link>
          <Button className={this.props.classes.login} /*onSubmit={this.submitLogout.bind(this)}*/ >
              Logout
          </Button>
        </Toolbar>
      </AppBar>
    </div>
    </ThemeProvider>
  );
}
}

// define types
NavBar.propTypes = {
  classes: PropTypes.object.isRequired,
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
  errors: PropTypes.object.isRequired
};

// allows us to get our state from Redux and map it to props
const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect (
  mapStateToProps,
  { logoutUser }  
)(withRouter(withStyles(useStyles)(NavBar)));
